import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_BASE = 'http://localhost:5000';

function App() {
  const [health, setHealth] = useState(null);
  const [headline, setHeadline] = useState('Winter Sale - 50% OFF');
  const [canvasState, setCanvasState] = useState({
    elements: [],
    width: 1080,
    height: 1080,
    backgroundColor: '#ffffff',
  });
  const [compliance, setCompliance] = useState(null);
  const [loadingCompliance, setLoadingCompliance] = useState(false);

  // Check backend health on load
  useEffect(() => {
    axios
      .get(`${API_BASE}/health`)
      .then(res => setHealth(res.data))
      .catch(() => setHealth({ status: 'error' }));
  }, []);

  // Handle image upload (add as one element on canvas)
  const handleImageUpload = e => {
    const file = e.target.files && e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = ev => {
      const src = ev.target.result;
      const newElement = {
        id: 'img-1',
        type: 'image',
        x: 200,
        y: 200,
        width: 400,
        height: 400,
        rotation: 0,
        opacity: 1,
        data: {
          src,
          elementType: 'product',
        },
      };

      setCanvasState(prev => ({
        ...prev,
        elements: [
          ...prev.elements.filter(el => el.id !== 'img-1'),
          newElement,
        ],
      }));
    };
    reader.readAsDataURL(file);
  };

  // Simple text add (headline) using editable headline state
  const handleAddText = () => {
    const textElement = {
      id: 'txt-1',
      type: 'text',
      x: 100,
      y: 50,
      width: 600,
      height: 80,
      rotation: 0,
      opacity: 1,
      data: {
        text: headline || 'Your Offer Here',
        fontSize: 32,
        fontFamily: 'Arial',
        color: '#000000',
      },
    };
    setCanvasState(prev => ({
      ...prev,
      elements: [
        ...prev.elements.filter(el => el.id !== 'txt-1'),
        textElement,
      ],
    }));
  };

  // Call backend compliance API
  const handleValidateCompliance = async () => {
    if (canvasState.elements.length === 0) {
      alert('Add an image or text first.');
      return;
    }

    setLoadingCompliance(true);
    setCompliance(null);
    try {
      const guidelines = {
        maxTextCoverage: 20,
        minLogoSize: 100,
        safeZoneMargin: 10,
        approvedColors: ['#000000', '#ffffff'],
      };

      const res = await axios.post(
        `${API_BASE}/api/creative/validate-compliance`,
        {
          canvasState,
          guidelines,
        }
      );

      setCompliance(res.data.compliance);
    } catch (err) {
      console.error(err);
      alert('Compliance API error. Check backend logs.');
    } finally {
      setLoadingCompliance(false);
    }
  };

  return (
    <div
      style={{
        fontFamily: 'system-ui',
        minHeight: '100vh',
        background: '#f5f5f5',
      }}
    >
      <header
        style={{
          background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
          color: '#fff',
          padding: '16px 24px',
        }}
      >
        <h1 style={{ margin: 0 }}>üé® CreativeForge AI</h1>
        <p style={{ margin: 0, fontSize: 14 }}>
          Backend:{' '}
          {health?.status === 'healthy' ? '‚úÖ Connected' : '‚ö† Not reachable'}
        </p>
      </header>

      <div style={{ padding: 20, display: 'flex', gap: 20 }}>
        {/* Left: Controls + Canvas */}
        <div style={{ flex: 3, display: 'flex', flexDirection: 'column', gap: 16 }}>
          <div
            style={{
              display: 'flex',
              gap: 10,
              flexWrap: 'wrap',
              marginBottom: 10,
              alignItems: 'center',
            }}
          >
            {/* Editable headline input */}
            <input
              type="text"
              value={headline}
              onChange={e => setHeadline(e.target.value)}
              placeholder="Enter headline (e.g. Diwali Mega Sale - 30% OFF)"
              style={{
                padding: 6,
                minWidth: 260,
                borderRadius: 4,
                border: '1px solid #ddd',
                marginRight: 8,
              }}
            />

            <label
              style={{
                padding: '8px 14px',
                background: '#667eea',
                color: '#fff',
                borderRadius: 6,
                cursor: 'pointer',
                fontSize: 14,
              }}
            >
              Upload Packshot
              <input
                type="file"
                accept="image/*"
                onChange={handleImageUpload}
                style={{ display: 'none' }}
              />
            </label>

            <button
              onClick={handleAddText}
              style={{
                padding: '8px 14px',
                background: '#764ba2',
                color: '#fff',
                border: 'none',
                borderRadius: 6,
                cursor: 'pointer',
                fontSize: 14,
              }}
            >
              Add Headline Text
            </button>

            <button
              onClick={handleValidateCompliance}
              disabled={loadingCompliance}
              style={{
                padding: '8px 14px',
                background: '#10b981',
                color: '#fff',
                border: 'none',
                borderRadius: 6,
                cursor: 'pointer',
                fontSize: 14,
                opacity: loadingCompliance ? 0.7 : 1,
              }}
            >
              {loadingCompliance ? 'Checking‚Ä¶' : 'Check Compliance'}
            </button>
          </div>

          {/* Simple visual canvas */}
          <div
            style={{
              background: '#fff',
              borderRadius: 8,
              padding: 10,
              boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
              overflow: 'auto',
            }}
          >
            <div
              style={{
                width: 540,
                height: 540,
                margin: '0 auto',
                position: 'relative',
                backgroundColor: canvasState.backgroundColor,
                border: '1px solid #ddd',
              }}
            >
              {canvasState.elements.map(el => (
                <div
                  key={el.id}
                  style={{
                    position: 'absolute',
                    left: el.x / 2,
                    top: el.y / 2,
                    width: el.width / 2,
                    height: el.height / 2,
                    opacity: el.opacity,
                    transform: `rotate(${el.rotation}deg)`,
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                  }}
                >
                  {el.type === 'image' && (
                    <img
                      src={el.data.src}
                      alt="creative-element"
                      style={{
                        width: '100%',
                        height: '100%',
                        objectFit: 'cover',
                      }}
                    />
                  )}
                  {el.type === 'text' && (
                    <div
                      style={{
                        fontSize: el.data.fontSize / 2,
                        fontFamily: el.data.fontFamily,
                        color: el.data.color,
                        textAlign: 'center',
                        padding: 4,
                      }}
                    >
                      {el.data.text}
                    </div>
                  )}
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Right: Compliance panel */}
        <div
          style={{
            flex: 1,
            background: '#fff',
            borderRadius: 8,
            padding: 16,
            boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
            maxWidth: 320,
          }}
        >
          <h3 style={{ marginTop: 0 }}>Compliance</h3>

          {compliance ? (
            <>
              <div
                style={{
                  fontSize: 32,
                  fontWeight: 'bold',
                  marginBottom: 10,
                  padding: 8,
                  borderRadius: 6,
                  textAlign: 'center',
                  background: compliance.isCompliant ? '#dcfce7' : '#fee2e2',
                  color: compliance.isCompliant ? '#15803d' : '#b91c1c',
                }}
              >
                {Math.round(compliance.score)}/100
              </div>

              {compliance.violations?.length > 0 && (
                <div style={{ marginBottom: 10 }}>
                  <h4 style={{ margin: '8px 0', color: '#b91c1c', fontSize: 13 }}>
                    Violations ({compliance.violations.length})
                  </h4>
                  {compliance.violations.map((v, idx) => (
                    <div
                      key={idx}
                      style={{
                        background: '#fee2e2',
                        borderLeft: '3px solid #b91c1c',
                        padding: 6,
                        marginBottom: 6,
                        fontSize: 12,
                      }}
                    >
                      <strong>{v.type}</strong>
                      <div>{v.message}</div>
                    </div>
                  ))}
                </div>
              )}

              {compliance.warnings?.length > 0 && (
                <div style={{ marginBottom: 10 }}>
                  <h4 style={{ margin: '8px 0', color: '#f59e0b', fontSize: 13 }}>
                    Warnings ({compliance.warnings.length})
                  </h4>
                  {compliance.warnings.map((w, idx) => (
                    <div
                      key={idx}
                      style={{
                        background: '#fffbeb',
                        borderLeft: '3px solid #f59e0b',
                        padding: 6,
                        marginBottom: 6,
                        fontSize: 12,
                      }}
                    >
                      {w.message}
                    </div>
                  ))}
                </div>
              )}

              {(!compliance.violations || compliance.violations.length === 0) &&
                (!compliance.warnings || compliance.warnings.length === 0) && (
                  <p style={{ fontSize: 13, color: '#15803d' }}>
                    No issues detected. Creative looks compliant.
                  </p>
                )}
            </>
          ) : (
            <p style={{ fontSize: 13, color: '#6b7280' }}>
              Upload an image and add text, then click ‚ÄúCheck Compliance‚Äù to see
              guideline validation.
            </p>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
